const API_URL = 'https://script.google.com/macros/s/AKfycbxDVuXZxzhgNQP3m4tGRngXVeNN1-yNSoXiRlOn_xBSq6WPpkvu96EjL5oQNv2jXcSDOg/exec'; // Ganti dengan URL kamu

async function getData(sheet) {
  const response = await fetch(`${API_URL}?sheet=${sheet}&action=read`);
  return await response.json();
}

async function addData(sheet, data) {
  await fetch(API_URL, {
    method: 'POST',
    body: JSON.stringify({sheet, action: 'add', ...data})
  });
}